import React, { useEffect, useState } from "react";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { Input, TextArea, FormBtn, label} from "../components/Form";

function AddStore() {
  // Setting our component's initial state
  const [stores, setStores] = useState([])
  const [formObject, setFormObject] = useState({})


  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };


  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.suburb && formObject.nameOfStore) {
      API.saveStore({
        suburb: formObject.suburb,
        nameOfStore: formObject.nameOfStore,
        phoneNumber: formObject.phoneNumber
      })
        .then(res => console.log(res))
        .catch(err => console.log(err));
    }
  };


    return (
      <Container fluid>
        <Row>
          <Col size="md-12">
            <Jumbotron>
              <h1>Add my store to the database</h1>
            </Jumbotron>
            <form>
              <Input
                onChange={handleInputChange}
                name="suburb"
                placeholder="Suburb (Required)"
              />
              <Input
                onChange={handleInputChange}
                name="nameOfStore"
                placeholder="Name (required)"
              />
              <Input
                onChange={handleInputChange}
                name="phoneNumber"
                placeholder="Number (required)"
              />


        <label>
          Pick your favorite flavor:
          {/* <select value={this.state.value}  */}
          onChange={handleInputChange}>
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          {/* </select> */}
        </label>




              <TextArea
                onChange={handleInputChange}
                name="pickupOrDelivery"
                placeholder="Details of Pickup or Delivery options for customers"
              />
              <FormBtn
                disabled={!(formObject.suburb && formObject.nameOfStore)}
                onClick={handleFormSubmit}
              >
                Submit
              </FormBtn>
            </form>
          </Col>

        </Row>
      </Container>
    );
  }


export default AddStore;
