import React, { useEffect, useState } from "react";
import Jumbotron from "../components/Jumbotron";
import DeleteBtn from "../components/DeleteBtn";
import API from "../utils/API";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";

function SuburbStores() {
  // Setting our component's initial state
  const [stores, setStores] = useState([])
  const [suburbSearch, setSuburbSearch] = useState([])
  const [formObject, setFormObject] = useState({})

  // Load all books and store them with setBooks
//   useEffect(() => {
//     loadBooks()
//   }, [])

  // Loads all books and sets them to books
//   function loadBooks() {
//     API.getBooks()
//       .then(res => 
//         setBooks(res.data)
//       )
//       .catch(err => console.log(err));
//   };

  // Deletes a book from the database with a given id, then reloads books from the db
//   function deleteBook(id) {
//     API.deleteBook(id)
//       .then(res => loadBooks())
//       .catch(err => console.log(err));
//   }

  // Handles updating component state when the user types into the input field
  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({...formObject, [name]: value})
  };

  // When the form is submitted, use the API.saveBook method to save the book data
  // Then reload books from the database
  function handleFormSubmit(event) {
    event.preventDefault();
    if (formObject.title && formObject.author) {
      API.saveStore({
        suburb: formObject.suburb,
        nameOfStore: formObject.nameOfStore,
        phoneNumber: formObject.phoneNumber
      })
        .then(res => console.log(res))
        .catch(err => console.log(err));
    }
  };

    return (
      <Container fluid>
        <Row>

          <Col size="md-6 sm-12">
            <Jumbotron>
              <h1>Search Stores In your Suburb</h1>
            </Jumbotron>

            <Col size="md-12">
            <form>
              <Container>
                <Row>
                  <Col size="xs-9 sm-10">
                    <Input
                      name="suburbSearch"
                      value={suburbSearch}
                      onChange={handleInputChange}
                      placeholder="Search your Suburb"
                    />
                  </Col>
                  <Col size="xs-3 sm-2">
                    <Button
                      onClick={handleFormSubmit}
                      type="success"
                      className="input-lg"
                    >
                        Search
                    </Button>
                  </Col>
                </Row>
              </Container>
            </form>
          </Col>

            {stores.length ? (
              <List>
                {stores.map(stores => {
                  return (
                    <ListItem key={stores._id}>
                      <a href={"/stores/" + stores._id}>
                        <strong>
                          {stores.suburb} by {stores.nameOfStore}
                        </strong>
                      </a>

                    </ListItem>
                  );
                })}
              </List>
            ) : (
              <h3>No Results to Display</h3>
            )}
          </Col>
        </Row>
      </Container>
    );
  }


export default SuburbStores;
